mapper.select:
SELECT D.*
FROM( 


	  AND A.DISTINGUISH=#{dis}
	  AND SYSDATE BETWEEN #{prev} AND #{next}+7
	   ) D
WHERE RNUM BETWEEN #{first} AND #{second}


WHERE A.DISTINGUISH=#{dis}
AND ISDELETE IS NULL
ORDER BY A.POSTDATE DESC

mapper.update:
UPDATE BOARD
SET TITLE=#{title},CONTENT=#{content},POSTDATE=SYSDATE,
HASFILE=NULL
HASFILE=#{hasfile}

WHERE BCODE=#{bcode}

UPDATE BOARD
SET HASFILE='y'
WHERE BCODE=#{bcode}

UPDATE BOARD
SET ISDELETE='y'
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET VIEWNUM=#{viewnum}
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET BEST=BEST+1,CAL=CAL+5
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET GOOD=GOOD+1,CAL=CAL+3
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET BAD=BAD+1,CAL=CAL-3
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET WORST=WORST+1,CAL=CAL-5
WHERE BCODE=#{bcode}

UPDATE BOARDPOINT
SET MEDAL=MEDAL+1
WHERE BCODE=#{bcode}

mapper.select:
SELECT COUNT(*)
FROM REPORTLIST
WHERE BCODE=#{bcode}
AND ID=#{reporter}
