mapper.update:
UPDATE COMMENTS
SET CONTENT=#{content},POSTDATE=SYSDATE
WHERE CCODE=#{ccode}

UPDATE COMMENTSPOINT 
SET GOOD=GOOD+1,CAL=CAL+3
WHERE CCODE=#{ccode}

UPDATE COMMENTSPOINT 
SET	BAD=BAD+1,CAL=CAL-3
WHERE CCODE=#{ccode}

UPDATE COMMENTSPOINT 
SET	MEDAL=MEDAL+1
WHERE CCODE=#{ccode}

mapper.select:
SELECT COUNT(*)
FROM CREPORTLIST
WHERE CCODE=#{ccode}
AND ID=#{reporter}

mapper.update:
UPDATE COMMENTS
SET CONTENT='삭제된 댓글입니다.', ISDELETE='y'
WHERE CCODE=#{ccode}
