mapper.select:
SELECT EXP
FROM MEMBER
WHERE ID=#{writerid}

SELECT RECOMPOINT
FROM MEMBER
WHERE ID=#{id}

SELECT HAVMEDAL
FROM MEMBER
WHERE MEMBERCODE=#{membercode}

mapper.update:
UPDATE MEMBER
SET HAVMEDAL=HAVMEDAL+#{medal}
WHERE
MEMBERCODE=#{membercode}

mapper.insert:
UPDATE MEMBER
SET DDARU=DDARU+#{ddaru}
WHERE
MEMBERCODE=#{membercode}

mapper.update:
UPDATE MEMBER
SET DDARU=#{ddaru}
WHERE
MEMBERCODE=#{membercode}

UPDATE MEMBER
SET PHOTO = ''
WHERE MEMBERCODE=#{membercode}

UPDATE MEMBER
SET LEVELITEM = 'Y'
WHERE MEMBERCODE=#{membercode}
